mapper.select:

	SELECT distinct v.rwid, v.sjhm, v.dxnr, v.wxzhid, v.qyhid, v.wxyyid FROM db_zgfz.v_dx_fs_md v
	WHERE v.fsfs = #{fsfs}


	AND v.tmid = #{tmid}


	AND v.tmid > 0


	ORDER BY v.rwid


	SELECT t.sqid || SUBSTR(t.sjhm, -3) AS vipid
	  FROM db_zgfz.t_wxqy_dzb t
	WHERE t.wxzhid = #{0}
	   	AND t.djxh = #{1}
	   	AND t.yxbz = 'Y'
